import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  MenuItem,
  CircularProgress
} from '@mui/material';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'faculty',
    school: '',
    phone: ''
  });

  const [showOTPInput, setShowOTPInput] = useState(false);
  const [otp, setOtp] = useState('');
  const [registrationEmail, setRegistrationEmail] = useState('');
  const [errors, setErrors] = useState({
    phone: '',
    email: '',
    form: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { register } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validate phone number for Indian format
    if (name === 'phone') {
      if (!/^[6-9]\d{9}$/.test(value)) {
        setErrors(prev => ({
          ...prev,
          phone: ''
        }));
      } else {
        setErrors(prev => ({ ...prev, phone: '' }));
      }
    }

    // Validate Woxsen email
    if (name === 'email') {
      if (!value.endsWith('@woxsen.edu.in')) {
        setErrors(prev => ({
          ...prev,
          email: 'Please use your Woxsen email address (@woxsen.edu.in)'
        }));
      } else {
        setErrors(prev => ({ ...prev, email: '' }));
      }
    }
  };

  const handleOTPSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('/api/auth/verify-email', {
        email: registrationEmail,
        otp
      });

      if (response.data.success) {
        // Store token and user data
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));

        // Redirect based on role
        if (response.data.user.role === 'admin') {
          navigate('/admin-dashboard');
        } else {
          navigate('/dashboard');
        }
      }
    } catch (error) {
      setError(error.response?.data?.message || 'Verification failed');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate before submission
    if (!formData.email.endsWith('@woxsen.edu.in')) {
      setErrors(prev => ({ ...prev, form: 'Please use your Woxsen email address' }));
      return;
    }

    if (!/^[6-9]\d{9}$/.test(formData.phone)) {
      setErrors(prev => ({ ...prev, form: 'Please enter a valid Indian phone number' }));
      return;
    }

    try {
      const response = await axios.post('/api/auth/register', formData);

      if (response.data.success) {
        setRegistrationEmail(formData.email);
        setShowOTPInput(true);
        setError('');
      }
    } catch (error) {
      setErrors(prev => ({ ...prev, form: error.response?.data?.message || 'An error occurred' }));
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8, mb: 4 }}>
        <Paper elevation={3} sx={{ p: 4 }}>
          <Typography variant="h4" align="center" gutterBottom>
            Register
          </Typography>
          {errors.form && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {errors.form}
            </Alert>
          )}
          {!showOTPInput ? (
            <form onSubmit={handleSubmit}>
              <TextField
                fullWidth
                label="Name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                label="Email"
                name="email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                margin="normal"
                required
                error={Boolean(errors.email)}
                helperText={errors.email || ''}
              />
              <TextField
                fullWidth
                label="Password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                select
                label="Role"
                name="role"
                value={formData.role}
                onChange={handleChange}
                margin="normal"
                required
              >
                <MenuItem value="student">Student</MenuItem>
                <MenuItem value="faculty">Faculty</MenuItem>
              </TextField>
              <TextField
                fullWidth
                label="School"
                name="school"
                value={formData.school}
                onChange={handleChange}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                label="Phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                margin="normal"
                required
                error={Boolean(errors.phone)}
                helperText={errors.phone || ''}
                inputProps={{
                  maxLength: 10,
                  pattern: '[6-9][0-9]{9}'
                }}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                sx={{ mt: 3 }}
              >
                Register
              </Button>
            </form>
          ) : (
            <form onSubmit={handleOTPSubmit}>
              <Typography variant="h6" gutterBottom>
                Email Verification
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ mb: 3 }}>
                Please enter the 6-digit OTP sent to your email ({registrationEmail})
              </Typography>
              <TextField
                fullWidth
                label="Enter OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                margin="normal"
                required
                inputProps={{
                  maxLength: 6,
                  inputMode: 'numeric',
                  pattern: '[0-9]*'
                }}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                disabled={loading || otp.length !== 6}
                sx={{ mt: 3, mb: 2 }}
              >
                {loading ? <CircularProgress size={24} /> : 'Verify OTP'}
              </Button>
              <Button
                fullWidth
                color="primary"
                onClick={() => {
                  setShowOTPInput(false);
                  setOtp('');
                  setError('');
                }}
              >
                Back to Registration
              </Button>
            </form>
          )}
          <Box sx={{ mt: 2, textAlign: 'center' }}>
            <Typography>
              Already have an account?{' '}
              <Button color="primary" onClick={() => navigate('/login')}>
                Login
              </Button>
            </Typography>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default Register;
